<div id="divInsertLinkPicker" style="position:absolute; display:none; left: -400px; top: -400px;">
<table id="htmltool_table" cellpadding=1 cellspacing=0 bgcolor="#000000">
<tr><td><table id="htmltool_table" cellpadding=2 cellspacing=0 bgcolor="#CCCCCC">
          <tr onmouseover="this.style.cursor='move'" onClick="MM_dragLayer('divInsertLinkPicker','',0,0,1000,25,true,false,-1,-1,-1,-1,false,false,0,'',false,'')" bgcolor="#888888"> 
            <td height="20" colspan="3" align="right"><table width="98%" height="15" border="0" cellpadding="0" cellspacing="0" id="htmltool_table">
                <tr> 
                  <td align="center"><strong><font color="#FFFFFF"></font></strong></td>
                  <td width="18" height="18"><img src="htmltool/bt_close.gif" width="16" height="14" onMouseOver="this.style.cursor='hand'" onClick="divInsertLinkPicker.style.display='none'"></td>
                </tr>
              </table></td>
          </tr>
          <tr> 
            <td width="60" align="right"><strong>URL</strong></td>
            <td align="left"><input name="inputInsertLinkPickerURL" type="text" class="htmltool_input_left" id="inputInsertLinkPickerURL" size="25" value="http://"></td>
            <td align="right" width="10">&nbsp;</td>
          </tr>
          <tr> 
            <td width="60" align="right"><strong>Target</strong></td>
            <td align="left"> <select name="inputInsertLinkPickerTarget" class="htmltool_select" id="inputInsertLinkPickerTarget">
                <option value="" selected> </option>
                <option value="_blank">_blank</option>
                <option value="_parent">_parent</option>
                <option value="_self">_self</option>
                <option value="_top">_top</option>
              </select></td>
            <td align="right">&nbsp;</td>
          </tr>
          <tr align="center"> 
            <td height="35" colspan="3"><input type="button" name="button" value="" class="htmltool_button_th_80"  onMouseOver="this.style.cursor='hand'" onClick="
with(document) {
	if(inputInsertLinkPickerURL.value=='') {
		inputInsertLinkPickerURL.focus();
		return false;
	}
	if(inputInsertLinkPickerURL.value=='http://') {
		inputInsertLinkPickerURL.focus();
		return false;
	}
}
fnSetInsertLinkPicker(inputInsertLinkPickerURL.value,inputInsertLinkPickerTarget.value);
"></td>
          </tr>
        </table></td></tr></table>
</div>
<div id="divImagePickerEdit" style="position:absolute; display:none; left: -400px; top: -400px;"> 
  <table id="htmltool_table" cellpadding=1 cellspacing=0 bgcolor="#000000">
    <tr> 
      <td><table id="htmltool_table" cellpadding=2 cellspacing=0 bgcolor="#CCCCCC">
          <tr onmouseover="this.style.cursor='move'" onClick="MM_dragLayer('divImagePickerEdit','',0,0,1000,20,true,false,-1,-1,-1,-1,false,false,0,'',false,'')" bgcolor="#888888"> 
            <td height="20" colspan="6" align="right"><table width="98%" height="15" border="0" cellpadding="0" cellspacing="0" id="htmltool_table">
                <tr> 
                  <td align="center"><strong><font color="#FFFFFF"></font></strong></td>
                  <td width="18" height="18"><img src="htmltool/bt_close.gif" width="16" height="14" onMouseOver="this.style.cursor='hand'" onClick="divImagePickerEdit.style.display='none'"></td>
                </tr>
              </table></td>
          </tr>
          <tr> 
            <td height="18" align="right"></td>
            <td height="18"><input name="inputImagePickerEditWidth" type="text" class="htmltool_input_left" id="inputImagePickerEditWidth" value="0" size="5"></td>
            <td width="30" height="18" align="left">pixel</td>
            <td height="18" align="right"></td>
            <td height="18" colspan="2"> <select name="inputImagePickerEditAlign" class="htmltool_select" id="inputImagePickerEditAlign">
                <option value="Default" selected></option>
                <option value="Left"></option>
                <option value="Right"></option>
                <option value="Top"></option>
                <option value="Middle"></option>
                <option value="Bottom"></option>
              </select></td>
          </tr>
          <tr> 
            <td height="18" align="right"></td>
            <td height="18"><input name="inputImagePickerEditHeight" type="text" class="htmltool_input_left" id="inputImagePickerEditHeight" value="0" size="5"></td>
            <td width="30" height="18" align="left">pixel</td>
            <td height="18" align="right"></td>
            <td width="10"><input name="inputImagePickerEditBorder" type="text" class="htmltool_input_left" id="inputImagePickerEditBorder" value="0" size="5"></td>
            <td height="18">pixel</td>
          </tr>
          <tr> 
            <td height="18" align="right"></td>
            <td height="18" colspan="5"><input name="inputImagePickerEditAlt" type="text" class="htmltool_input_left" id="inputImagePickerEditAlt" size="35"></td>
          </tr>
          <tr align="center"> 
            <td height="30" colspan="6"><input name="btInsertImage" type="button" class="htmltool_button_th_120" id="btInsertImage" value="" onMouseOver="this.style.cursor='hand'" onClick="
with(document) {
	if(inputImagePickerEditWidth.value=='') { inputImagePickerEditWidth.value=0; inputImagePickerEditWidth.focus(); return false;}
	if(isNaN(inputImagePickerEditWidth.value*1)) { inputImagePickerEditWidth.value=0; inputImagePickerEditWidth.focus(); return false;}
	if(inputImagePickerEditHeight.value=='') { inputImagePickerEditHeight.value=0; inputImagePickerEditHeight.focus(); return false;}
	if(isNaN(inputImagePickerEditHeight.value*1)) { inputImagePickerEditHeight.value=0; inputImagePickerEditHeight.focus(); return false;}
	if(inputImagePickerEditBorder.value=='') { inputImagePickerEditBorder.value=0; inputImagePickerEditBorder.focus(); return false;}
	if(isNaN(inputImagePickerEditBorder.value*1)) { inputImagePickerEditBorder.value=0; inputImagePickerEditBorder.focus(); return false;}
	fnUpdateImage(inputImagePickerEditAlt.value,inputImagePickerEditAlign.value,inputImagePickerEditBorder.value,inputImagePickerEditWidth.value,inputImagePickerEditHeight.value);
}
				  "></td>
          </tr>
        </table></td>
    </tr>
  </table>
</div>